import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  topics: {},
};

const topicsSlice = createSlice({
  name: "topics",
  initialState,
  reducers: {
    addTopic({ id, name, icon }) {
      state.topics = {
        [id]: {
          id: id,
          name: name,
          icon: icon,
          quizIds: [],
        },
      };
    },
  },
});

export const { addTopic } = topicsSlice.actions;
export default topicsSlice.reducer;
