import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  topics: {},
};

const topicsSlice = createSlice({
  name: "topics",
  initialState,
  reducers: {
    addTopic: (state, action) => {
      const { id, name, icon } = action.payload;
      state.topics = {
        topics: {
          [id]: {
            id: id,
            name: name,
            icon: icon,
            quizIds: [],
          },
        },
      };
    },
  },
});

export const { addTopic } = topicsSlice.actions;
export default topicsSlice.reducer;
